body {
  font-family: Cambria, Cochin, Georgia, Times, "Times New Roman", serif;
  height: 100vh;
  background-color: #ccc;
}

.colors-container {
  width: 300px;
  height: 300px;
  background-color: #000;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 15px;
  border-radius: 50%;
  position: relative;
}

.colors {
  width: 90%;
  height: 90%;
  transition: filter 150ms;
}
.colors--active {
  filter: brightness(150%);
}

.color-a {
  background-color: #30c782;
  border-top-left-radius: 100%;
  place-self: end;
}

.color-b {
  background-color: #d43128;
  border-top-right-radius: 100%;
  place-self: end start;
}

.color-c {
  background-color: #e8cf3f;
  border-bottom-left-radius: 100%;
  place-self: start end;
}

.color-d {
  background-color: #1797d8;
  border-bottom-right-radius: 100%;
  place-self: start;
}

.options {
  position: absolute;
  width: 150px;
  height: 150px;
  background-color: #000;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  border-radius: 100%;
  display: grid;
  place-items: center;
}
.options .title {
  color: #fff;
  font-size: 1.2em;
  text-transform: uppercase;
}

.player-turn {
  cursor: pointer;
}

.form {
  width: 60%;
}
.form--hidden {
  display: none;
}
.form__field {
  padding: 1em;
  display: flex;
  justify-content: space-between;
}
.form__field label {
  text-transform: uppercase;
}
.form__submit {
  padding: 5px 10px;
  background: none;
  background-color: tomato;
  outline: none;
  border: none;
  color: #fff;
  text-transform: uppercase;
  display: block;
  margin: auto;
}
.form__submit:hover {
  cursor: pointer;
  background-color: #fff;
  color: tomato;
}

.ranking {
  background-color: #fff;
  border: 1px solid #000;
  border-radius: 2px;
  padding: 3px;
}
.ranking__head {
  padding: 10px;
  font-weight: normal;
  color: #fff;
}
.ranking__head--row {
  background-color: #ff6565;
}
.ranking__body {
  padding: 5px;
  border: 1px solid orange;
}
.ranking__body--bold {
  font-weight: bold;
}
.ranking__body--rows:nth-child(odd) {
  background-color: #fcf7da;
}

