body {
  font-family: Cambria, Cochin, Georgia, Times, "Times New Roman", serif;
  height: 100vh;
  background-image: linear-gradient(90deg, #00ff88 0%, #61efff 100%);
}

.logout {
  position: absolute;
  right: 5px;
  top: 5px;
  background: none;
  border: none;
  border-radius: 5px;
  padding: 5px 10px;
  cursor: pointer;
  background-color: #000;
  color: #fff;
}
.logout--disabled {
  display: none;
}
.logout:hover {
  outline: #fff 2px solid;
}

.game {
  margin-left: auto;
  margin-right: auto;
  width: 320px;
  height: 320px;
  background-color: #000;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1.5em;
  border-radius: var(--gameboard-border-r2);
  position: relative;
}
.game__color {
  width: 90%;
  height: 90%;
  transition: background-color 200ms, transform 100ms;
}
.game__color--a {
  background-color: var(--gameboard-color-1);
  border-top-left-radius: var(--gameboard-border-r3);
  border-top-right-radius: var(--gameboard-border-r1);
  border-bottom-left-radius: var(--gameboard-border-r1);
  place-self: end;
}
.game__color--b {
  background-color: var(--gameboard-color-2);
  border-top-right-radius: var(--gameboard-border-r3);
  border-top-left-radius: var(--gameboard-border-r1);
  border-bottom-right-radius: var(--gameboard-border-r1);
  place-self: end start;
}
.game__color--c {
  background-color: var(--gameboard-color-3);
  border-bottom-left-radius: var(--gameboard-border-r3);
  border-top-left-radius: var(--gameboard-border-r1);
  border-bottom-right-radius: var(--gameboard-border-r1);
  place-self: start end;
}
.game__color--d {
  background-color: var(--gameboard-color-4);
  border-bottom-right-radius: var(--gameboard-border-r3);
  border-bottom-left-radius: var(--gameboard-border-r1);
  border-top-right-radius: var(--gameboard-border-r1);
  place-self: start;
}
.game__color--active {
  background-color: #fff;
}
.game__color--player-turn {
  cursor: pointer;
}
.game__color--player-turn:active {
  transform: scale(0.9);
}
.game__options {
  position: absolute;
  width: 50%;
  height: 50%;
  padding: 15px;
  background-color: #000;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  border-radius: var(--gameboard-border-r2);
  display: grid;
  grid-template-rows: 30% 1fr;
  justify-content: center;
  place-items: center;
}
.game__title {
  margin: 0;
  color: #fff;
  font-size: 1.2em;
  text-transform: uppercase;
}

.form {
  height: 100%;
  display: grid;
  align-content: space-between;
}
.form__player-name {
  text-align: center;
  color: peachpuff;
  margin-bottom: 0;
  margin-top: 0;
  font-size: 1em;
}
.form__label {
  color: #eee;
  text-transform: lowercase;
  margin-right: 3px;
}
.form__input {
  color: #eee;
  width: 60%;
  border: none;
  background: none;
  border-bottom: 1px solid #eee;
}
.form__radio-container {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 5px;
}
.form__radio {
  -moz-appearance: none;
  -webkit-appearance: none;
  border: none;
  border: 2px solid;
  border-radius: 10%;
  cursor: pointer;
  transition: background-color 300ms, color 300ms;
  font-size: 8px;
  padding: 5px;
  text-align: center;
}
.form__radio:checked {
  color: #fff;
}
.form__radio:disabled {
  border-color: grey;
  color: grey;
}
.form__radio--a {
  border-color: var(--gameboard-color-1);
  color: var(--gameboard-color-1);
}
.form__radio--a::before {
  content: "NORMAL";
}
.form__radio--a:checked {
  background-color: var(--gameboard-color-1);
}
.form__radio--b {
  border-color: var(--gameboard-color-3);
  color: var(--gameboard-color-3);
}
.form__radio--b::before {
  content: "HARD";
}
.form__radio--b:checked {
  background-color: var(--gameboard-color-3);
}
.form__radio--c {
  border-color: var(--gameboard-color-2);
  color: var(--gameboard-color-2);
}
.form__radio--c::before {
  content: "MANIAC";
}
.form__radio--c:checked {
  background-color: var(--gameboard-color-2);
}
.form__submit {
  width: 50%;
  justify-self: center;
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
}
.form__submit:disabled {
  color: gray;
  cursor: initial;
}

.ranking {
  padding: 3px;
  color: var(--font-color-1);
  border-collapse: collapse;
  background-image: linear-gradient(90deg, #432371 0%, #faad7a 100%);
  margin-left: auto;
  margin-right: auto;
  margin-top: 20px;
  border-radius: 10px;
}
.ranking__head {
  padding: 0.8em;
  font-weight: normal;
}
.ranking__head:nth-child(2) {
  font-weight: bold;
}
.ranking__head:nth-child(4) {
  font-weight: bold;
}
.ranking__head:nth-last-child(1) {
  display: none;
}
@media screen and (min-width: 600px) {
  .ranking__head:nth-last-child(1) {
    display: table-cell;
  }
}
.ranking__body {
  padding: 5px;
}
.ranking__body:nth-child(2) {
  font-weight: bold;
}
.ranking__body:nth-child(3) {
  text-align: center;
}
.ranking__body:nth-child(4) {
  font-weight: bold;
  text-align: end;
  padding-right: 1em;
}
.ranking__body:nth-last-child(1) {
  display: none;
}
@media screen and (min-width: 600px) {
  .ranking__body:nth-last-child(1) {
    display: table-cell;
  }
}
.ranking__body--rows:nth-child(odd) {
  background-color: var(--leaderboard-body-color-1);
}
.ranking__body--rows:nth-child(even) {
  background-color: var(--leaderboard-body-color-2);
}

.active-row {
  animation: recent-score 1s infinite;
}

@keyframes recent-score {
  50% {
    background-color: var(--leaderboard-active-color);
  }
}

