      border-top-right-radius: $gameboard-border-r1;
      place-self: start;
    }

    &--active {
      background-color: #fff;
    }

    &--player-turn {
      cursor: pointer;
    }

    &--player-turn:active {
      transform: scale(.9);
    }
    
  }

  &__options {
    position: absolute;
    width: 50%;
    height: 50%;
    padding: 15px;
    background-color: #000;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    border-radius: $gameboard-border-r2;
    display: grid;
    grid-template-rows: 30% 1fr;
    justify-content: center;
    place-items: center;
  }

  &__title {
    margin: 0;
    color: #fff;
    font-size: 1.2em;
    text-transform: uppercase;
  }

}

